export declare const styles = "\n:host {\n    --wallet-button-bg: #fefefe;\n    --wallet-button-text: #000;\n    --wallet-button-border: #ff9900;\n    --wallet-button-hover-bg: #ff9900;\n    --wallet-font-family: \"Arial\", sans-serif;\n    --wallet-width: 184px;\n    --wallet-height: 48px;\n}\n\n.wallet-logout-button{\n    height: var(--wallet-height);\n    display: flex;\n    justify-content: center;\n    align-items: center;\n    width: var(--wallet-height);\n}\n\n.wallet-address {\n    height: var(--wallet-height);\n    display: flex;\n    justify-content: center;\n    align-items: center;\n    font-family: var(--wallet-font-family);\n    font-weight: 500;\n    background-color: var(--wallet-button-bg);\n    color: var(--wallet-button-text);\n    border: 2px solid var(--wallet-button-border);\n    padding: 0 15px;\n    font-size: 16px;\n    cursor: pointer;\n    transition: background 0.3s, transform 0.2s;\n    gap: 8px;\n    border-radius: 8px;\n}\n\n\n.wallet-button {\n    width: var(--wallet-width);\n    height: var(--wallet-height);\n    display: flex;\n    justify-content: center;\n    align-items: center;\n    font-family: var(--wallet-font-family);\n    font-weight: 500;\n    background-color: var(--wallet-button-bg);\n    color: var(--wallet-button-text);\n    border: 2px solid var(--wallet-button-border);\n    padding: 0 15px;\n    font-size: 16px;\n    cursor: pointer;\n    transition: background 0.3s, transform 0.2s;\n    gap: 8px;\n    border-radius: 8px;\n}\n\n.wallet-button:hover {\n    background-color: var(--wallet-button-hover-bg);\n    transform: scale(1.05);\n}\n\n.wallet-container {\n    position: relative;\n    display: flex;\n    gap: 8px;\n    z-index: 50;\n    align-items: center;\n}\n\n.wallet-address {\n    padding: 0 16px;\n    border-radius: 8px;\n    border: 2px solid var(--wallet-button-border);\n    background: var(--wallet-button-bg);\n    text-align: center;\n}\n\n.wallet-logout-button {\n    background: var(--wallet-button-hover-bg);\n    border: 2px solid var(--wallet-button-border);\n    border-radius: 8px;\n    cursor: pointer;\n    transition: all 0.3s ease-in-out;\n    padding: 0;\n}\n\n.wallet-logout-button:hover {\n    transform: scale(1.05);\n}\n\n.logout-icon {\n    width: 24px;\n    height: 24px;\n}\n\n.wallet-dropdown {\n    position: absolute;\n    background: #ffffff;\n    border: 2px solid var(--wallet-button-border);\n    border-radius: 8px;\n    width: var(--wallet-width);\n    box-shadow: 0px 4px 8px rgba(0, 0, 0, 0.1);\n    top: 60px;\n    font-weight: 500;\n}\n\n.wallet-dropdown.hidden {\n    display: none;\n}\n\n.hidden {\n    display: none;\n}\n\n.wallet-dropdown-item {\n    display: flex;\n    align-items: center;\n    gap: 10px;\n    padding: 10px;\n    cursor: pointer;\n    transition: all 0.3s ease-in-out;\n    font-weight: 500;\n}\n\n.wallet-dropdown-item:hover {\n    background: #ff9900;\n    color: #fff;\n}\n\n.wallet-item-icon {\n    width: 24px;\n    height: 24px;\n}\n";
